package com.danycarreto.layouts.ConstraintLayoutimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.widget.Toastimport com.danycarreto.layouts.*import com.danycarreto.layouts.RelativeLayout.MainActivityclass ConstraintLayoutActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_constraint_layout)        var name:String = ""        var value:Int = 0        intent.extras?.let {            name = it.getString(EXTRA_NAME, "Default Principal")            value = it.getInt(EXTRA_ID, 0)        }        Toast.makeText(this,            "${ConstraintLayoutActivity::class.java.simpleName} name: $name & value: $value",            Toast.LENGTH_SHORT).show()    }    override fun onBackPressed() {        val intent = Intent()        intent.putExtra("type", "constraint")        setResult(REQUEST_CONSTRAINT, intent)        finish()    }}